import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { addUniversity } from '../../../truffle/addUni'; // Adjust path

const UniversitySignIn = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [universityHash, setUniversityHash] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Fixed university address
  const universityAddress = "0xFFcf8FDEE72ac11b5c542428B35EEF5769C409f0";

  const handleSubmit = async (e) => {
     e.preventDefault(); 
     console.log("Usuario:", username); 
     console.log("Contraseña:", password); 
     const universityAddress = "0x90F8bf6A479f320ead074411a4B0e7944Ea8c9C1"; // Fixed address 
     try { 
        const response = await fetch("http://localhost:5000/addUniversity", {
           method: "POST",
           headers: { "Content-Type": "application/json" }, 
           body: JSON.stringify({ 
            address: universityAddress, 
            username: username, 
            password: password 
          }), 
        }); 
        const data = await response.json(); 
        if (data.success) { 
          console.log("University added successfully. Hash:", data.hash); 
        } else { 
          console.error("Failed to add university:", data.error); 
        } 
      } catch (error) { 
        console.error("Error making API request:", error); 
      } 
    }; 

  return (
    <div style={registerPageStyle}>
      <h2>University Sign In</h2>
      <form onSubmit={handleSubmit} style={formStyle}>
        <div style={inputGroupStyle}>
          <label htmlFor="username" style={labelStyle}>User:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
            style={inputStyle}
          />
        </div>
        <div style={inputGroupStyle}>
          <label htmlFor="password" style={labelStyle}>Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={inputStyle}
          />
        </div>
        <button type="submit" style={submitButtonStyle} disabled={loading}>
          {loading ? "Submitting..." : "Submit"}
        </button>
      </form>

      {/* Show result */}
      {universityHash && <p style={{ color: "green" }}> University Hash: {universityHash}</p>}
      {error && <p style={{ color: "red" }}> {error}</p>}

      {/* Redirect Link */}
      <div>
        <Link to="/University/UniversityLogIn" style={submitButtonStyle}>Go to Log In</Link>
      </div>
    </div>
  );
};

// Styles
const registerPageStyle = {
  maxWidth: '400px',
  margin: '50px auto',
  padding: '20px',
  border: '1px solid #ccc',
  borderRadius: '8px',
  textAlign: 'center',
};

const formStyle = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  justifyContent: 'center',
};

const inputGroupStyle = {
  marginBottom: '15px',
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  width: '100%',
};

const labelStyle = {
  marginBottom: '5px',
  fontSize: '14px',
  textAlign: 'center',
  width: '100%',
};

const inputStyle = {
  padding: '8px',
  fontSize: '16px',
  borderRadius: '4px',
  border: '1px solid #ccc',
  width: '40%',
};

const submitButtonStyle = {
  padding: '10px 20px',
  backgroundColor: '#4CAF50',
  color: 'white',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
  width: '150px',
  marginTop: '20px',
  textDecoration: "none"
};

export default UniversitySignIn;
