import React, { useState } from 'react';
import { Link } from 'react-router-dom'; // Import Link to redirect to University Sign In page

const UniversityLogin = () => {

  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  // Method that handles the login form
  const handleSubmit = async (e) => {
    e.preventDefault(); 
    console.log('Usuario:', username); // Prints user
    console.log('Contraseña:', password); // Prints password

    const universityAddress = "0x90F8bf6A479f320ead074411a4B0e7944Ea8c9C1"; // Fixed address

    try {
      const response = await fetch("http://localhost:4000/consult", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          address: universityAddress,
          username: username,
          password: password, 
          type: 1
        }),
      });

      const data = await response.json();
    } catch (error) {
      setMessage("Error making API request.");
      console.error("Error making API request:", error);
    }
  };


  return (
    <div style={loginPageStyle}>
      <h2>University Log In</h2>
      <form onSubmit={handleSubmit} style={formStyle}>
        <div style={inputGroupStyle}>
        <label htmlFor="username" style={labelStyle}>User:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
            style={inputStyle}
          />
        </div>
        <div style={inputGroupStyle}>
        <label htmlFor="password" style={labelStyle}>Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={inputStyle}
          />
        </div>

        {/* Sign In Link */}
        <div style={linkContainerStyle}>
          <Link to="/University/UniversitySignIn" style={linkStyle}>Sign In</Link>
        </div>

        {/* Redirect to University Home Page on button click */}
        <div style={submitButtonStyle}>
          <Link to= "/University/UniversityPages/UniversityHome" style={submitButtonStyle}>Submit</Link>
        </div>
      </form>
    </div>
  );
};

// Styles
const loginPageStyle = {
  maxWidth: '400px',
  margin: '50px auto', // Center the form horizontally and add space from top
  padding: '20px',
  border: '1px solid #ccc',
  borderRadius: '8px',
  textAlign: 'center', // Center the text inside the form
};

/* Form*/
const formStyle = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',     
  justifyContent: 'center', 
};

const inputGroupStyle = {
  marginBottom: '15px',
  display: 'flex',
  flexDirection: 'column',  // Stack label and input vertically
  alignItems: 'center', // Align label and input to the left
  width: '100%', // Make it take full width
};

/* Label */
const labelStyle = {
  marginBottom: '5px', // Add space between label and input
  fontSize: '14px',
  textAlign: 'center', // Align the label text to the left
  width: '100%', // Ensure label takes full width
};

const inputStyle = {
  padding: '8px',
  fontSize: '16px',
  borderRadius: '4px',
  border: '1px solid #ccc',
  width: '40%', // Make the input field take full width
};

/* Submit button*/
const submitButtonStyle = {
  padding: '10px 20px',
  backgroundColor: '#4CAF50',
  color: 'white',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
  width: '150px', 
  marginTop: '20px', // Add space between inputs and button
};

/* Sign In */
const linkContainerStyle = {
  marginBottom: '10px', // Space between the link and button
};

const linkStyle = {
  color: '#007bff',
  fontSize: '16px',
  textDecoration: 'underline', // Makes the link underlined
  cursor: 'pointer',
};

export default UniversityLogin;
